%% Roi_Poly()% LISCOMP Lab 2021 - 2022, https://liscomp.dima.unige.it% -------------------------------------------------------------------------% DESCRIPTION: % This function plots a polygonal ROI using Put_Roi() and sets commands in % the ROI panel.% -------------------------------------------------------------------------%%%% called by: main_gui_brain(), panel 'ROI' (dropdown menu)%%%% call: Put_Roi()function Roi_Poly()global gui_ROI;RoiKind='impoly';RoiPosition=[236,236;236,246;236,266;246,266;256,266;266,266;266,256;266,236];Put_Roi(RoiKind,RoiPosition);set(gui_ROI.PANELroi.PANEL3.PB2,'enable','on');end